import React, { useState } from 'react';
import './App.scss';
import InputField from './components/InputField/InputField';
import SectionHeader from './components/SectionHeader/SectionHeader';
import ContactCard from './components/ContactCard/ContactCard';

const App: React.FC = () => {
  const [form, setForm] = useState({givenName: '', lastName: ''});

  const onFormChange = (e: any) => {
    setForm({...form, [e.target.name]: e.target.value});
  }

  const getContactInfo = (form: any) => {
    return {
        name: `${form.givenName} ${form.lastName}`,
        phone: form.phone,
        address1: `${form.house} ${form.street}`,
        address2: `${form.suburb ? `${form.suburb}, `: ''}${form.state}`,
        postcode: form.postcode,
        country: form.country
      };
  }

  return (
    <div className="hcard-builder">
      <div className="hcard-builder-container">
        <div className="form-container">
          <h1>hCard Builder</h1>
          <SectionHeader label="Personal details"></SectionHeader>
          <div className="form-row">
            <InputField label="Given name" name="givenName" type="text" onChange={onFormChange}/>
            <InputField label="Last name" name="lastName" type="text" onChange={onFormChange}/>
          </div>
          <div className="form-row">
            <InputField label="Email" name="email" type="email" onChange={onFormChange}/>
            <InputField label="Phone" name="phone" type="tel" onChange={onFormChange}/>
          </div>
          <SectionHeader label="Address"></SectionHeader>
          <div className="form-row">
            <InputField label="House name or #" name="house" type="text" onChange={onFormChange}/>
            <InputField label="Street" name="street" type="text" onChange={onFormChange}/>
          </div>
          <div className="form-row">
            <InputField label="Suburb" name="suburb" type="text" onChange={onFormChange}/>
            <InputField label="State" name="state" type="text"/>
          </div>
          <div className="form-row">
            <InputField label="Postcode" name="postcode" type="text" onChange={onFormChange}/>
            <InputField label="Country" name="country" type="text" onChange={onFormChange}/>
          </div>
        </div>
        <div className="preview-container">
          <ContactCard contact={{name: `${form.givenName} ${form.lastName}`}}/>
        </div>
      </div>
    </div>
  );
}

export default App;
